mapper.select:
select student.id, student.name, no, gender, phone, degree, id_card, is_college, address, graduation_date,
violation_count, violation_score, clazz.id as clazz_id, student.create_time, student.update_time, clazz.name as clazz_name
from student
left join clazz on student.clazz_id = clazz.id


        and student.name like concat('%', #{name}, '%')


        and student.degree = #{degree}


        and student.clazz_id = #{clazzId}


limit #{offset}, #{pageSize}
